mapper.select:
/* 아이디중복확인 */
SELECT count(*)
FROM USERS
WHERE user_id=#{userId}

/* 비밀번호 확인 */
SELECT count(*)
FROM USERS
WHERE user_id=#{userId} and user_pw=#{userPw}

mapper.update:
/*정보 수정 */
UPDATE USERS  

   user_id = #{userId}
    , user_pw = #{userPw}
    , user_email = #{userEmail}
    , user_phone = #{userPhone}


WHERE user_id=#{userId}

/* 회원 탈퇴 */
	UPDATE USERS
	SET user_delete = 1
	WHERE user_id=#{userId} and user_pw=#{userPw}
